package com.ctsi.ssdc.errorcode.core.service;


import com.ctsi.ssdc.errorcode.core.dto.ErrorCodeAutoGenerateReqDTO;
import com.ctsi.ssdc.errorcode.domain.CscpErrorCode;

import javax.validation.Valid;
import java.time.ZonedDateTime;
import java.util.List;

/**
 * 错误码 Framework Service 接口
 *
 * @author hx
 */
public interface ErrorCodeFrameworkService {

    /**
     * 自动创建错误码
     *
     * @param autoGenerateDtos 错误码信息
     */
    void autoGenerateErrorCodes(@Valid List<ErrorCodeAutoGenerateReqDTO> autoGenerateDtos);

    /**
     * 增量获得错误码数组
     *
     * 如果 minUpdateTime 为空时，则获取所有错误码
     *
     * @param applicationName 应用名
     * @param minUpdateTime 最小更新时间
     * @return 错误码数组
     */
    List<CscpErrorCode> getErrorCodeList(String applicationName, ZonedDateTime minUpdateTime);

}
